        this.rest = rest;
        this.data = data;
        this.router = router;
        this.btnDisable = false;
        this.url = 'http://localhost:8080/login';
        this.employee = new Employee();
    }
    ngOnInit() { }
    validate() {
        return true;
    }
    async login() {
        this.btnDisable = true;
        if (this.validate()) {
