import { __decorate } from "tslib";
import { Component } from '@angular/core';
let OtpComponent = class OtpComponent {
    constructor(rest, data, router) {
        this.rest = rest;
        this.data = data;
        this.router = router;
        this.btnDisable = false;
        this.url = 'localhost:8080/api/user/info/change-password';
        this.changePassword = new ChangePassword();
    }
    ngOnInit() { }
    validate() {
        return true;
    }
    async changePass() {
        this.btnDisable = true;
        if (this.validate()) {
            this.rest
                .post(this.url, this.changePassword)
                .then((data) => {
                let value = data;
                localStorage.getItem('token');
                // await this.data.getProfile();
                this.router.navigate(['/user/otp']);
            })
                .catch((error) => {
                this.data.error(error['error']);
                this.btnDisable = false;
            });
        }
    }
};
OtpComponent = __decorate([
    Component({
        selector: 'app-otp',
        templateUrl: './otp.component.html',
        styleUrls: ['./otp.component.css']
    })
], OtpComponent);
export { OtpComponent };
